INSERT INTO microbiology_questions (question_text, option_a, option_b, option_c, option_d, option_e, correct_answer) VALUES
    ('безпосередньо розмножуються в клітинах імунної системи і руйнують їх. Виберіть серед перерахованих ті захворювання, при яких має місце вищезгадане:', '. Інфекційний мононуклеоз, СНІД', '. Туберкульоз, мікобактеріоз', '. Поліомієліт, гепатит А', '. Дизентерія, холера', '. Ку-гарячка, висипний тиф', 'A'),
    ('Після проведення туберкулінової проби (проба Манту) у дитини 10-ти через 48 годин на місці введення туберкуліну утворилася папула до 10 мм у діаметрі. Який механізм гіперчутливості лежить в основі розвитку вказаних змін?', '.Клітинна цитотоксичність', '. Гранулематоз', '. Імунокомплексна цитотоксичність', '. Антитілозалежна цитотоксичність', '. Анафілаксія', 'A'),
    ('На 8-й день після введення протиправцевої сироватки з приводу брудної рани стопи у пацієнта підвищилася температура тіла до 380С, з’явилися біль в суглобах, висипка, свербіж. У крові – лейкопенія і тромбоцитопенія. Який тип алергічної реакції розвинувся?', '. Імунокомплексна', '. Анафілактична', '. Цитотоксична', '. Стимулююча', '. Гіперчутливість уповільненого типу', 'A'),
    ('Лікування туберкульозу здійснюється за допомогою комбінованої хіміотерапії, що включає речовини різного механізму дії. Який з протитуберкульозних засобів пригнічує в мікобактеріях транскрипцію РНК на ДНК?', '. Рифампіцин', '. ПАСК', '. Ізоніазид', 'о інфекційного відділення лікарні госпіталізовано хворого з діагнозом бактеріальної дизентерії.', '. Стрептоміцин', 'A'),
    ('Значну роль у профілактиці туберкульозу відіграє планова масова вакцинація проти туберкульозу новонароджених дітей віком 5-7 днів життя. При цьому застосовують вакцину:', '.БЦЖ', 'організм людини введено живу вакцину. На підвищення активності яких клітин сполучної тканини', '. АДП', '. АД', '. АКП', 'A'),
    ('Збудник гепатиту Д (Дельта-агент) є дефектним вірусом, який може реплікуватись лише в клітинах, що вже інфіковані одним з вірусів. Який саме вірус необхідний для розмноження дельта-вірусу?', '. Вірус гепатиту В', 'рач-бактериолог выделил у больного ребенка возбудителя дизентерии Флекснера -–тип', '. Вірус гепатиту Е', 'изентерія', '.Вірус імунодефіциту людини', 'A'),
    ('При огдяді хворої лікар-гінеколог відмітив симптомами запалення статевих шляхів. У мазку, взятому із піхви виявлено овально-грушоподібні найпростіші з шипом, з передньої частини яких відходять джгутики; наявна ундулююча мембрана. Яке захворювання підозрює лікар у хворої?', '.Урогенітальний трихомоноз', '. Балантидіоз', '. Токсоплазмоз', '. Лямбліоз', '. Кишковий трихомоноз', 'A'),
    ('такі реактиви: кардіоліпіновий антиген, спиртовий екстракт ліпоїдів із серцевого м‘яза бика з холестерином, антиген з трепонем, зруйнованих ультразвуком, гемолітична система, фізіологічний розчин, досліджувані сироватки. Який ще компонент необхідний для постановки реакції?', '. Комплемент', '. Живі трепонеми', '. Еритроцити барана', '. Діагностична преципітуюча сироватка', '. Антиглобулінова сироватка', 'A'),
    ('У 65-річного мужчини розвинувся гнійний абсцес на шиї. Виділена культура грампозитивних коків, яка має плазмокоагулазну активність. Скоріше всього - це:', 'Staphylococcus aureus', 'Streptococcus pyogenes', 'Staphylococcus epidermidis', 'Staphylococcus saprophyticus', '-', 'A'),
    ('частому нагноєнні післяопераційних ран. При бактеріологічному дослідженні гною був виділений золотистий стафілокок. Яке дослідження треба використати для виявлення джерела цього збудника серед персоналу відділення.', 'Фаготипування', 'Мікроскопічний', 'Серологічна ідентифікація', 'Визначення чутливості до антибіотиків', 'Біохімічна ідентифікація', 'A'),
    ('Значну роль у профілактиці туберкульозу відіграє планова масова вакцинація проти туберкульозу новонароджених дітей віком 5-7 днів життя. При цьому застосовують вакцину:', '.БЦЖ', 'першому класі було проведене медичне обстеження учнів з метою відбору дітей для ревакцинації проти', '. АДП', '. АД', '. АКП', 'A'),
    ('Лікування туберкульозу здійснюється за допомогою комбінованої хіміотерапії, що включає речовини різного механізму дії. Який з протитуберкульозних засобів пригнічує в мікобактеріях транскрипцію РНК на ДНК?', '. Рифампіцин', 'бактеріологічну лабораторію направлено харкотиння хворого на туберкульоз, Для', '. Ізоніазид', '. Етіонамід', '. Стрептоміцин', 'A')
